import React from 'react';
import Typography from '@mui/material/Typography'; // Corrected import for Typography
import CssBaseline from '@mui/material/CssBaseline'; // Corrected import for CssBaseline
import Container from '@mui/material/Container';
import PropTypes from 'prop-types'; // Import PropTypes for prop validation

function Header(props) {
    const { title, backgroundColor } = props; // Destructure props

    return (
        <div className="header-bar" style={{ backgroundColor, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        <CssBaseline />
            <Container maxWidth="lg" sx={{ padding: '8px', backgroundColor: 'transparent'}}>
                <Typography variant="h2" component="h1" gutterBottom style={{ textAlign: 'center', marginTop: 10}}>
                    {title}
                </Typography>
            </Container>
        </div>
    );
}

Header.propTypes = {
  title: PropTypes.string.isRequired, // Title is a required string prop
  backgroundColor: PropTypes.string, // Background color is an optional prop
};

export default Header;
