import './App.css';
import SearchBar from '../SearchBar/SearchBar';
import SearchResults from '../SearchResults/SearchResults';
import Playlist from '../Playlist/Playlist';
import { createTheme, ThemeProvider, Typography, CssBaseline } from '@mui/material/styles';
// Import Container if you decide to use it
import Container from '@mui/material/Container';

// Custom MUI theme
const theme = createTheme({
  palette: {
    primary: {
      main: '#ed2939', // Your imperial red
    },
    secondary: {
      main: '#bfc0c0', // Your silver sand
    },
    background: {
      default: '#12191e', // Your rich black
      paper: '#2a3439', // Your gunmetal
    },
    text: {
      primary: '#e5e5e5', // Your platinum
      secondary: '#6b554b', // Your umber
    },
  },
  typography: {
    fontFamily: "'Open Sans', sans-serif",
    h1: {
      fontSize: '2rem', // Example size
    },
    // Add other typography styles as needed
  },
  components: {
    // Customizing specific components (example with Button)
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 4, // Example style override
          // Add other overrides as needed
        },
      },
    },
  },
});


function App() {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline /> {/* This resets browser defaults and applies your background */}
      <div className="App">
        {/* Using a Typography component for the main heading */}
        <Typography variant="h1" component="h1" gutterBottom>
          Jammming
        </Typography>
        {/* Optional: Use a Container to control the width and centering */}
        <Container maxWidth="lg"> 
          <SearchBar />
          <SearchResults />
          <Playlist />
        </Container>
      </div>
    </ThemeProvider>
  );
}

export default App;
