import React from 'react';
import Typography from '@mui/material/Typography';
import Paper from '@mui/material/Paper';
import { makeStyles } from '@mui/material/styles';


const useStyles = makeStyles((theme) => ({
  header: {
    display: 'flex',
    alignItems: 'center', // Center vertically
    justifyContent: 'center', // Center horizontally
    padding: theme.spacing(2), // Add padding for spacing
    backgroundColor: theme.palette.primary.main, // Set your header background color here
  },
  title: {
    color: 'white', // Set the text color
    fontSize: '2rem', // Adjust the font size as needed
    fontWeight: 'bold', // Set font weight if desired
  },
}));

function Header({ title, backgroundColor }) {
  const classes = useStyles();

  return (
    <Paper className={classes.header} style={{ backgroundColor }}>
      <Typography variant="h1" component="h1" className={classes.title}>
        {title}
      </Typography>
    </Paper>
  );
}

export default Header;
