import React from 'react';
import './Playlist.css';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Tracklist from '../TrackList/TrackList';

function Playlist() {
  return (
    <div className="playlist">
      <h1 className="playlist-title">My Playlist</h1>
      <Tracklist />
      <TextField 
        fullWidth 
        size="small" 
        label="New Playlist" 
        variant="outlined"
        sx={{ 
          backgroundColor: 'rgba(255, 255, 255, 0.15)', // Semi-transparent background
          marginBottom: '10px', // Consistent margin bottom
          borderRadius: '4px', // Rounded corners
          color: 'white' // Text color
        }}
      />
      <Button 
        variant="contained" 
        color="primary"
        sx={{ 
          marginY: 2, // Add spacing above and below the button
          backgroundColor: '#ed2939', // Use the color from your palette
          '&:hover': {
            backgroundColor: '#c82333' // Darker shade for hover state
          },
        }}
      >
        SAVE TO SPOTIFY
      </Button>
    </div>
  );
}

export default Playlist;
