import './App.css';
import SearchBar from '../SearchBar/SearchBar';
import SearchResults from '../SearchResults/SearchResults';
import Playlist from '../Playlist/Playlist';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import Typography from '@mui/material/Typography'; // Corrected import for Typography
import CssBaseline from '@mui/material/CssBaseline'; // Corrected import for CssBaseline
import Grid from '@mui/material/Grid'; 

// Custom MUI theme
const theme = createTheme({
  palette: {
    primary: {
      main: '#ed2939', // Your imperial red
    },
    secondary: {
      main: '#bfc0c0', // Your silver sand
    },
    background: {
      default: '#12191e', // Your rich black
      paper: '#2a3439', // Your gunmetal
    },
    text: {
      primary: '#e5e5e5', // Your platinum
      secondary: '#6b554b', // Your umber
    },
  },
  typography: {
    fontFamily: "'Open Sans', sans-serif",
    h1: {
      fontSize: '2rem', // Example size
    },
    // Add other typography styles as needed
  },
  components: {
    // Customizing specific components (example with Button)
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 4, // Example style override
          // Add other overrides as needed
        },
      },
    },
  },
});


function App() {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <div className="App">
        <Typography variant="h2" component="h1" gutterBottom>
          Jammming
        </Typography>
        <SearchBar />
        <Grid container spacing={3} justifyContent="center"> {/* Use Grid container */}
          <Grid item xs={12} md={6}> {/* Grid item for SearchResults */}
            <SearchResults />
          </Grid>
          <Grid item xs={12} md={6}> {/* Grid item for Playlist */}
            <Playlist />
          </Grid>
        </Grid>
      </div>
    </ThemeProvider>
  );
}

export default App;
