// Track.js
import React from 'react';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
import Button from '@mui/material/Button';

function Track({ isRemoval, onAdd, onRemove, track }) { // Assuming track is an object containing details of the song
  return (
    <ListItem className="track">
      <ListItemText primary={track.name} secondary={`${track.artist} | ${track.album}`} />
      <Button 
        variant="contained" 
        color={isRemoval ? "secondary" : "primary"} // Change color based on isRemoval
        onClick={isRemoval ? () => onRemove(track.id) : () => onAdd(track)}
        sx={{ marginLeft: 1 }} // Add left margin for spacing
      >
        {isRemoval ? 'Remove' : 'Add'} // Button text based on isRemoval
      </Button>
    </ListItem>
  );
}

export default Track;
